import React, { useState, useEffect } from "react";
import Axios from 'axios'
import apiService from "apiService";


function ProductDashboard() {

  const [productName, setProductName] = useState('')
  const [productPrice, setProductPrice] = useState('')
  const [productList, setProductList] = useState([])
  const [newPrice, setNewPrice] = useState('')
  const [newName, setNewName] = useState('')

  useEffect(()=> {
    fetch('http://localhost:3001/api/get/')
    .then((resp) => resp.json())
    .then((resp) => setProductList(resp))
    .catch((error) => console.log(error));
  }, [productList]);



  const submitProduct = () => {
    apiService.postProducts({productName, productPrice}).then(response=>setProductList([...productList, response]))
     .then(()=>{
      alert("successfull inserted");
     });
  };

  const deleteProduct = (product) =>{
    Axios.delete(`http://localhost:3001/api/delete/${product}`);
  }

  const updateProduct = (product) =>{
    Axios.put("http://localhost:3001/api/update/", {
      id: product,
      productName: newName,
      productPrice: newPrice,
    });
    setNewName("")
    setNewPrice("")
  };

  return (
    <div className="App">
      <h1>Products</h1>
      <div className='form'> 
      <label>Product Name:</label>
      <input type="text" name="productName" onChange={(e)=>{
        setProductName(e.target.value)
      }}/> <br></br>
      <label>Price Name:</label>
      <input type="number" name="productPrice" onChange={(e)=>{
        setProductPrice(e.target.value)
      }}/>
      
      <button onClick = {()=>submitProduct()}>Submit</button>

      Products
      {productList.map((val)=>{
        return <div className="card">
          <h1>{val.productName}</h1>
          <p>Price: {val.productPrice}</p>

          <button onClick = {()=>{deleteProduct(val.id)}}>Delete</button> <br></br>
          <input type="text" onChange={(e)=> {
            setNewName(e.target.value)
          }}/> <br></br>
          <input type="number" onChange={(e)=> {
            setNewPrice(e.target.value)
          }}/>
          <button onClick = {()=>{updateProduct(val.id)}}>Update Product</button>
          </div>
      })}
      </div>

    </div>
  );
}

export default ProductDashboard;
