import React, { useState, useEffect } from "react";
import './App.css';
import Axios from 'axios'

function App() {

  const [productName, setProductName] = useState('')
  const [productPrice, setProductPrice] = useState('')
  const [productList, setProductList] = useState([])

  useEffect(()=> {
      Axios.get('http://localhost:3001/api/get').then((response)=>{
        setProductList(response.data);
      })
  }, [productList])

  //console.log(productList);

  const submitProduct = () => {
    Axios.post('http://localhost:3001/api/insert',{
      productName: productName, 
      productPrice: productPrice,
    }).then(response=>setProductList([...productList, response]))
    .then(()=>{
      alert("successfull inserted");
    })
  };

  return (
    <div className="App">
      <h1>Products</h1>
      <div className='form'> 
      <label>Product Name:</label>
      <input type="text" name="productName" onChange={(e)=>{
        setProductName(e.target.value)
      }}/> <br></br>
      <label>Price Name:</label>
      <input type="number" name="productPrice" onChange={(e)=>{
        setProductPrice(e.target.value)
      }}/>

      <button onClick = {submitProduct()}>Submit</button>

      Products
      {productList.map((val)=>{
        return <h1>Product Name: {val.productName} | Product Price: {val.productPrice}</h1>
      })}
      </div>
    </div>
  );
}

export default App;
