import React, { useState, useEffect } from "react";
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';
import apiService from "./apiService";



function SellProduct(){

    const [productList, setProductList] = useState([])
    const [selectedProductList, setSelectedProductList] = useState([])
    let selectedItemID = []

    const [itemToSend, setItemToSend] = useState('')


    useEffect(()=> {
        fetch('http://localhost:3001/api/get/')
        .then((resp) => resp.json())
        .then((resp) => setProductList(resp))
        .catch((error) => console.log(error));
      }, []);

      const [selects, setselects] = useState();
      if(!isNaN(selects)){
        selectedItemID.push(parseInt(selects))
        
      }
      
      let unique = [...new Set(selectedItemID)];
      
      selectedItemID.length = 0;
      selectedItemID.push.apply(selectedItemID, unique);

      console.log(selectedItemID)

      const remove = (id) => {
        const index = selectedItemID.indexOf(id);
        console.log(index)
        if(index !=0){
          const b = selectedItemID[0];
          selectedItemID[0] = selectedItemID[index];
          selectedItemID[index] = b; 
        }
        selectedItemID.shift()
        console.log(selectedItemID)
        setSelectedProductList([])
      }

      const sell = () => {
        selectedItemID.map((val)=>{
          console.log(val)
          apiService.postSold({val}).then(response=>setItemToSend(""))
        .then(()=>{
         alert("successfull inserted");
        });
        })
        
      }

      
      
    return(
        <div className="App">
            <select value={selects} onChange={e=>setselects(e.target.value)}>
              <option select disabled={true}>--Select Options--</option>
              {productList.map((result)=>(<option value={result.id}>{result.productName}</option>))}
      </select>
          <h1>Selected Item</h1>

          {productList.map((val)=>{
            if(selectedItemID.includes(val.id)){
              return <div>
                     {val.productName} Price: {val.productPrice}  
                     <button onClick = {()=>{remove(val.id)}}>Remove</button>
              </div>
            }
      })}
          <button onClick = {()=>{sell()}}>Sell</button>
        </div>
    );
}
export default SellProduct;
