import { Button } from "@mui/material";
import React, { useState, useEffect } from "react";

function Report() {

    const [soldProducts, setSoldProducts] = useState([])

    useEffect(()=> {
        fetch('http://localhost:3001/api/report/')
        .then((resp) => resp.json())
        .then((resp) => setSoldProducts(resp))
        .catch((error) => console.log(error));
      }, []);

      const sort = () => {
        var a = [
          {
              "Name" : "Sachin",
              "Age"  : "41",
              "Team" : "Mumbai"
          },
          {
              "Name" : "Dravid",
              "Age"  : "42",
              "Team" : "Rajasthan"
          },
          {
              "Name" : "Yuvraj",
              "Age"  : "31",
              "Team" : "Bangalore"
          }
      ];

         a.sort(function(x,y){return y["Age"]-x["Age"]});
         console.log(a);
        console.log(soldProducts)
      }



    return(
        <div>
            Sold Products
            {soldProducts.map((val)=>{
        return <div>
          <h1>{val.productName} Total Sold: {val.soldProductQuantity}</h1>
        </div>
            })}

          <Button variant="outlined" onClick = {()=>{sort()}}>Sell</Button>
            </div>
    )

}

export default Report;
