import React, { useState, useEffect } from "react";
import Axios from 'axios'
import apiService from "./apiService";
import { BrowserRouter, Routes, Route, useNavigate } from "react-router-dom";

import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import Slide from '@mui/material/Slide';
import Avatar from '@mui/material/Avatar';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Link from '@mui/material/Link';
import Paper from '@mui/material/Paper';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import { createTheme, ThemeProvider } from '@mui/material/styles';

const theme = createTheme();


function ProductDashboard() {

  const navigate = useNavigate();


  const [productName, setProductName] = useState('')
  const [productPrice, setProductPrice] = useState('')
  const [productList, setProductList] = useState([])
  const [newPrice, setNewPrice] = useState('')
  const [newName, setNewName] = useState('')



  useEffect(()=> {
    if(localStorage.getItem('loggedin') !== 'true'){
      navigate('/');
    }
    fetch('http://localhost:3001/api/get/')
    .then((resp) => resp.json())
    .then((resp) => setProductList(resp))
    .catch((error) => console.log(error));
  }, [productList]);

  //console.log(localStorage.getItem('loggedin'))



  const submitProduct = () => {
    apiService.postProducts({productName, productPrice}).then(response=>setProductList([...productList, response]))
     .then(()=>{
      alert("successfull inserted");
     });
  };

  const deleteProduct = (product) =>{
    Axios.delete(`http://localhost:3001/api/delete/${product}`);
  }

  const logout = () => {
    localStorage.removeItem('loggedin')
  }

  const updateProduct = (product) =>{
    Axios.put("http://localhost:3001/api/update/", {
      id: product,
      productName: newName,
      productPrice: newPrice,
    });
    setNewName("")
    setNewPrice("")
  };

  return (
    <div className="App">
      <Button variant="outlined" onClick = {()=>{logout()}}>Logout</Button> <br></br>
      Add Products
      <div className='form'> 
      <label>Product Name:</label>
      <input type="text" name="productName" onChange={(e)=>{
        setProductName(e.target.value)
      }}/> <br></br>
      <label>Price:</label>
      <input type="number" name="productPrice" onChange={(e)=>{
        setProductPrice(e.target.value)
      }}/>

      <Button variant="outlined" onClick = {()=>submitProduct()}>Submit</Button>

      Selected Products
      <table>

      {productList.map((val)=>{
        return <div>
          <tr>
            <th>
            <h1>{val.productName}</h1>
          <p>Price: {val.productPrice}</p>
            </th>
          <th>
          <Button variant="outlined" onClick = {()=>{deleteProduct(val.id)}}>Delete</Button> <br></br>
          <input type="text" onChange={(e)=> {
            setNewName(e.target.value)
          }}/> <br></br>
          <input type="number" onChange={(e)=> {
            setNewPrice(e.target.value)
          }}/>
          <br></br>
          <Button variant="outlined" onClick = {()=>{updateProduct(val.id)}}>Update Product</Button>
          </th>


          </tr>
          </div>
      })}
      </table>
      </div>
    </div>
  );
}

export default ProductDashboard;
