import React, { useState, useEffect } from "react";
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';



function SellProduct(){

    const [productList, setProductList] = useState([])
    const options = [{}];
    const [selectedItemID] = useState([]);

    useEffect(()=> {
        fetch('http://localhost:3001/api/get/')
        .then((resp) => resp.json())
        .then((resp) => setProductList(resp))
        .catch((error) => console.log(error));
      }, []);

      productList.map((e)=>{
        options.push({"id": e.id, "name": e.productName})
      })

      console.log(options.id)

      const [selects, setselects] = useState();
      if(!selectedItemID.includes(selects)) selectedItemID.push(selects)
      

      console.log(selectedItemID)

    return(
        <div className="App">
            <select value={selects} onChange={e=>setselects(e.target.value)}>
              <option selected disabled="true">--Select Options--</option>
              {productList.map((result)=>(<option value={result.id}>{result.productName}</option>))}
      </select>
          <h1>Selected Item</h1>

          {productList.map((val)=>{
        return <div>
          <h1>{val.productName}</h1>
          <p>Price: {val.productPrice}</p>

          </div>
      })}


        </div>
    
    );
}
export default SellProduct;
