import React, { useState, useEffect } from "react";
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';



function SellProduct(){

    const [productList, setProductList] = useState([])
    const options = [{}];
    this.state = {
      id: 0
    };

    this.handleChange = this.handleChange.bind(this);

    useEffect(()=> {
        fetch('http://localhost:3001/api/get/')
        .then((resp) => resp.json())
        .then((resp) => setProductList(resp))
        .catch((error) => console.log(error));
      }, []);

      productList.map((e)=>{
        options.push({"id": e.id, "name": e.productName})
      })

      console.log(options.id)

      const addToCart = (id) =>{
        console.log(id)
      }

      const handleChange = (e) => {
        console.log("Fruit Selected!!");
        this.setState({ id: e.target.value });
      }



    return(
        <div className="App">
            <select>
              <option selected disabled="true" value={this.state.id} onChange={this.handleChange}>--Select Options--</option>
              {productList.map((result)=>(<option value={result.id}>{result.productName}</option>))}
    
      </select>
        </div>
    
    );
}
export default SellProduct;
