import { Button } from "@mui/material";
import React, { useState, useEffect } from "react";

function Report() {

    const [soldProducts, setSoldProducts] = useState([])

    useEffect(()=> {
        fetch('http://localhost:3001/api/report/')
        .then((resp) => resp.json())
        .then((resp) => setSoldProducts(resp))
        .catch((error) => console.log(error));
      }, []);

      const sort = () => {
        var data = soldProducts; // assign your data here
        var arr = [];
        for(let key in data){
        data[key]["key"] = key;
        arr.push(data[key]);

        arr.sort(function(a,b) { return parseInt(a.SortOrder)-parseInt(b.SortOrder)});
        console.log(arr)
      }

} 


    return(
        <div>
            Sold Products
            {soldProducts.map((val)=>{
        return <div>
          <h1>{val.productName} Total Sold: {val.soldProductQuantity}</h1>
        </div>
            })}

          <Button variant="outlined" onClick = {()=>{sort()}}>Sell</Button>
            </div>
    )

}

export default Report;
